import React, {useRef, useState} from 'react'
import './Video.css'

import stock from './Hway.mp4'
function Video() {
    const[playing, setPlaying] = useState(false)
    const videoRef = useRef(null)

    const handleVideoPress = () =>
    {
        if(playing){
            videoRef.current.pause();
            setPlaying(false);
        } else {
            videoRef.current.play();
            setPlaying(true);

        }         
    };
        // if video is playing stop it...

        // otherwise if its not  playing
        // play it 
    return (
        <div className ='video'>
            <video 
            onClick={handleVideoPress}
            className="video__player"
            videoRef={videoRef}
            loop 
            src={stock}
            >
            </video>

            {/* <video 
            className="video_player"
            loop 
            src="https://player.vimeo.com/external/443134297.sd.mp4?s=38a2a9882596ccdc09a5da35855a37a20a6d5b78&profile_id=164">
            </video> */}

            {/* VideoFooter */}
            {/* videoSidebar */}
        </div>
    )
}

export default Video


